INSERT INTO `novel` VALUES ('零之曲 二度悲歌', '世界-神观之梦', '同人|悬疑|永远的7日之都', '7509', '<1万', '2', '已完结', '【永远的七日之都】-零-相关。主线中登场的零是以活骸的形式，偶然间我就想到，要是我们玩家手中的零也有可能变成活骸呢？那么指挥使会做些什么来阻止悲剧的再度发生呢？抱着这样的想法我投入了创作。', '2018-08-12 09:56', '2018-08-12 10:02', 'http://www.linovel.net/book/105196.html');
