INSERT INTO `novel` VALUES ('天宫绯色宝鉴', 'Shined-Kalpa', '恋爱|后宫|悬疑', '1.6万', '3.3万', '23', '连载中', '一本华丽的鉴录书  \n  一个痴迷养成和放置的理工青年     \n\n  是拯救恐怖古堡的真祖新娘？  \n 邂逅世界之底的祭品精灵公主？   \n还是猎户座的种茶小姐姐？  \n\n  什么什么？ \n你只是想怀念高中时隔壁班的姑娘？ \n好吧，那就变成她来攻略你自己吧！', '2018-07-04 14:24', '2018-08-04 21:14', 'http://www.linovel.net/book/104595.html');
