INSERT INTO `novel` VALUES ('异世界的读书生涯', '林风无言', '幻想|异界|后宫', '3.5万', '11.5万', '88', '连载中', '刚刚度过中考的我，因为阎罗王的一时疏忽，生死簿上的名字被划掉了。由于歉意，阎罗王将我转生在一个由魔力主导发展的异世界。没有修炼的天赋，但阎罗王却给予了我一个系统，只要认真读书，也能成为高阶位的强者。', '2018-06-22 12:52', '2018-07-11 06:00', 'http://www.linovel.net/book/104515.html');
