INSERT INTO `novel` VALUES ('苍穹之眼', '渣荒', '幻想|战斗|异界', '4232', '2.2万', '14', '连载中', '贫困的某事务性组织，突然接到电话，要其刺杀帝国首脑，纠结中，却因为价格高昂而同意，但其中却牵扯出其他古怪的东西，看似风光的帝国，实际上却显得有些摇摇欲坠。', '2018-06-26 10:37', '2018-06-27 10:58', 'http://www.linovel.net/book/104536.html');
