INSERT INTO `novel` VALUES ('神之基因', 'Hiroshi', '战斗|科幻|超能力', '1.4万', '3.3万', '33', '连载中', '在未来，人类发现了神之基因，当人类注射了这种基因后有一定几率会获得神的力量，男主人公作为在全是神之基因拥有者的部队，会有怎么样的生活呢？', '2018-06-12 16:05', '2018-06-27 23:27', 'http://www.linovel.net/book/104454.html');
