INSERT INTO `novel` VALUES ('在名为星辰的彼岸，绝望的风呼啸，而划过流星的少女对狼微笑。', 'asd65198805', '幻想|恋爱|3月征文', '6081', '6.5万', '45', '连载中', '毁灭的天幕之下，弱小的人类战斗的故事。\n死去的末世之中，相遇的两人恋爱的故事。', '2018-03-08 17:46', '2018-04-08 14:44', 'http://www.linovel.net/book/103834.html');
