INSERT INTO `novel` VALUES ('平行世界P.W.', 'GhostNoer', '校园|科幻', '2.1万', '12万', '37', '连载中', '你是否思考过，假设世界不止一个，那将会怎样？某年的某个秋天，刚刚升入高中的少年周泽的记忆中突然出现了不属于自己的记忆碎片。这些碎片是什么？来自哪里？这些碎片又将会怎样的影响到周泽的生活？系列小说企划Eden Project第一作，世界观进行了严谨构架但又不失有趣的科幻小说《平行世界P.W.》就此展开！', '2018-01-02 22:27', '2018-03-11 21:02', 'http://www.linovel.net/book/103530.html');
