INSERT INTO `novel` VALUES ('人类消亡之时', '葵猫', '科幻|治愈|妹控|11月月度征文活动', '3.6万', '11.7万', '78', '连载中', '灾难隐藏在喧嚣的人群中\n是病毒？诅咒？还是神罚？\n人类在那瞬间如雪花落在掌心，消失殆尽。\n只剩下他和她。', '2017-11-23 01:20', '2017-12-29 22:23', 'http://www.linovel.net/book/103300.html');
