INSERT INTO `novel` VALUES ('樱', '夢の終わり', '日常|第二回新人赏  短篇组', '1.1万', '7.8万', '36', '连载中', '灰色的生活和麻木的感觉，每天同样的生活方式使人的目光变得简单。冬天的灰白色终会被春天的樱色所取代，而在这之中，需要的就是对自己以往生活方式的不断剖析。', '2017-07-07 09:24', '2017-07-07 11:42', 'http://www.linovel.net/book/102366.html');
