INSERT INTO `novel` VALUES ('醒来发现我长了根数据线', '异彩_长弓手地鼠', '科幻|悬疑', '2.9万', '10.8万', '51', '连载中', '陈科是个22岁的普通网络人，对于他而言生活就像是一碗早已经让人麻木不仁的中药。而当一个奇怪的梦之后，他发现他的身体里接出来了一根数据线。之后他的世界发生了天翻地覆的变化。。。', '2016-08-19 17:24', '2016-09-07 10:29', 'http://www.linovel.net/book/101034.html');
