INSERT INTO `novel` VALUES ('日常崩坏见闻录', '筋肉恶魔幽香', '搞笑|日常|妖怪', '1.8万', '10.3万', '31', '连载中', '我的名字叫秦蛰磷，我很普通，我很普通，我很普通，因为很重要所以要说三遍！我的梦想是过上平凡的生活，而不是跟着整天被始皇帝蹭脸、不是被年兽当成敌人追杀、不是被麒麟当成未婚夫暖床、不是被某僵尸当成伙伴征服世界、更不是成为佛教门徒！', '2016-07-25 21:14', '2016-07-27 20:44', 'http://www.linovel.net/book/100888.html');
