INSERT INTO `novel` VALUES ('深海', '行走于境界的蝴蝶', '战斗|科幻|同人|第一回轻小说新人赏 中篇', '13.9万', '14万', '90', '连载中', '这是发生在那个舰娘和深海栖舰们无休止战斗中的世界的故事，也是那个世界众生的故事\n向漫画《舰c的一些事》致敬', '2016-06-10 08:29', '2016-07-07 14:06', 'http://www.linovel.net/book/100532.html');
