INSERT INTO `novel` VALUES ('当猩红之雨降临时', '夜山不是夜酸也不是夜三', '异界|第一回轻小说新人赏 中篇', '5.4万', '11.4万', '44', '连载中', '“你是人嘛？”\n  “不，我不是人。”\n  “那你会伤害我吗？”\n  “不，我会赌上性命保护你。”\n  “......”', '2016-06-09 08:33', '2016-06-09 15:01', 'http://www.linovel.net/book/100522.html');
