INSERT INTO `novel` VALUES ('死者编码', '王有水', '幻想|战斗|超能力|第一回轻小说新人赏 中篇', '6752', '15万', '57', '连载中', '如果说你所熟识的世界只是一片无序的编码，你会怎样？\n我想你也许会疯掉的吧。\n但是有一群身为乱码与回收站里残留物的人却这样说着\n“那就去改写它！”', '2016-03-25 20:31', '2016-05-15 22:01', 'http://www.linovel.net/book/100068.html');
