INSERT INTO `novel` VALUES ('交易', 'inuyashaym', '幻想|第二回新人赏  短篇组', '6505', '7.7万', '38', '连载中', '从古至今你情我愿的事情有很多，买卖就是其中一件，我给予你之所需，你给我相同的回报，这就是公平的交易，现实世界什么都可以买卖，唯独时间，因为时间是造物主的所有物，只有造物主才有支配权，而这个世界的造物主明叫無，無主把世间所有的时间分给四个使者，分别管理东西南北四方之下所有的一切......', '2017-07-09 21:04', '2017-07-10 13:02', 'http://www.linovel.net/book/102394.html');
